if length(ARGS) == 0
    bits = 6
else
    bits = parse(Int64, ARGS[1])
end

DFiP = DistFix{2+bits, bits}

t = @timed pr(@alea begin
            isEffective = flip(0.5)
            probControl = uniform(DFiP, 0.0, 1.0)
            probTreated = uniform(DFiP, 0.0, 1.0)
            probAll = uniform(DFiP, 0.0, 1.0)

            if isEffective
                for i in 1:100
                    controlGroupFlip = parametrised_flip(probControl)
                    observe(controlGroupFlip == dataControlGroup[i])
                    treatedGroupFlip = parametrised_flip(probTreated)
                    observe(treatedGroupFlip == dataTreatedGroup[i])
                end
            else
                for i in 1:100
                    controlGroupFlip = parametrised_flip(probAll)
                    observe(controlGroupFlip == dataControlGroup[i])
                    treatedGroupFlip = parametrised_flip(probAll)
                    observe(treatedGroupFlip == dataTreatedGroup[i])
                end
            end
            isEffective
end)


p = t.value

io = open(string("./benchmarks/clinicalTrial1/results.txt"), "a")
@show bits, p, t.time
writedlm(io, [bits p t.time], ",")  
close(io)